(хай [N (+ 1 1000) ]
   (петля [nom (ціле 1) den (ціле 1)  idx 1 sum 0]
     (якщо (> idx N) 
       [nom  den idx sum]
       (повторись (+ nom den den) (+ den nom) (+ 1 idx) (add-dec-longer sum nom den)) 
     )
   )  
)
